create database Doan_WebsiteTuyenDung
use Doan_WebsiteTuyenDung

create table Job_Seeker(
	JS_id varchar(30) primary key not null,
	JS_name nvarchar(30) not null,
	JS_email varchar(30) not null,
	JS_password varchar(30) not null,
	JS_phone varchar(11),
	JS_address nvarchar(100),
	JS_skills nvarchar(MAX),
	JS_expectedSalary money,
	JS_image nvarchar(MAX)
)

create table Employer(
	E_id varchar(30) primary key not null,
	E_name nvarchar(30) not null,
	E_email nvarchar(30) not null,
	E_password varchar(30) not null,
	E_contactPerson nvarchar(30),
	E_phone varchar(11),
	E_address nvarchar(100),
	E_about nvarchar(MAX),
	E_image nvarchar(MAX)
)


create table Job(
	J_id varchar(30) primary key not null,
	J_title nvarchar(100) not null,
	J_description nvarchar(MAX) not null,
	J_requiredSkills nvarchar(MAX) not null,
	J_salary money not null,
	J_postDate date default(GETDATE()) not null,
	J_expirationDate date not null,
	J_status int,
	J_Location nvarchar(100),
	J_Type nvarchar(10) not null,
	E_id varchar(30) not null CONSTRAINT FK_Job 
			FOREIGN KEY (E_id) REFERENCES Employer(E_id)
)

create table Application(
	A_id varchar(30) primary key not null,
	A_appliedDate date default(GETDATE()) not null,
	A_status int not null,
	A_feedBack nvarchar(100),
	J_id varchar(30) not null CONSTRAINT FK_App 
			FOREIGN KEY (J_id) REFERENCES Job(J_id)
)

create table Resume(
	R_id varchar(30) primary key not null,
	R_name nvarchar(30) not null,
	R_updateDate date default(GETDATE()) not null,
	R_content nvarchar(MAX) not null,
	R_fileName nvarchar(255) NOT NULL,
    R_filePath nvarchar(255) NOT NULL,
	JS_id varchar(30) not null 
	CONSTRAINT FK_Resume 
			FOREIGN KEY (JS_id) REFERENCES Job_Seeker(JS_id)
)

create table Intermediate_table(
	R_id varchar(30) not null,
	A_id varchar(30) not null,
	primary key(R_id,A_id),
	CONSTRAINT FK_Intermediate 
			FOREIGN KEY (R_id) REFERENCES Resume(R_id),
	CONSTRAINT FK_Intermediate1 
			FOREIGN KEY (A_id) REFERENCES Application(A_id),
)

INSERT [dbo].[Employer] ([E_id], [E_name], [E_email], [E_password], [E_contactPerson], [E_phone], [E_address], [E_about], [E_image]) VALUES (N'e01', N'AHT Tech', N'trandai1116@gmail.com', N'123', NULL, N'0886601884', N'Hà Nội', N'<p>AHT Tech hiện là một trong những công ty công nghệ tiên phong cung cấp các giải pháp công nghệ trong lĩnh vực Du lịch cho khách sạn ở Việt Nam và khu vực Đông Nam Á, là đối tác của nhiều Tập đoàn lớn như Vingroup, FLC, Sungroup, TNG Group, TTC ... Quy mô công ty trên 70 người, với Trụ sở chính ở Singapore, Văn phòng chính ở Hà Nội và 5 chi nhánh trên cả nước. Mức tăng trưởng trung bình 300%/năm liên tục trong vòng 5 năm, AHT Tech luôn là đơn vị mang lại các giải pháp quản trị tối ưu cho lĩnh vực Du lịch ở Việt Nam và Đông Nam Á. Với mục tiêu trở thành "Công ty công nghệ số 1 trong lĩnh vực Travel Tech không chỉ ở khu vực Đông Nam Á mà cả trên thế giới", chúng tôi luôn tìm kiếm và chào đón những nhân sự tài năng luôn cố gắng vươn đến những tầm cao mới, không ngại thay đổi và luôn luôn sẵn sàng đương đầu với thử thách.</p>', N'https://cdn.topcv.vn/80/company_logos/cong-ty-co-phan-aht-tech-63c4f49e02261.jpg')
INSERT [dbo].[Employer] ([E_id], [E_name], [E_email], [E_password], [E_contactPerson], [E_phone], [E_address], [E_about], [E_image]) VALUES (N'e02', N'Unica', N'khanhnguyen@gmail.com', N'123', NULL, N'05741232341', N'Hồ Chí Minh', N'<p>Công ty cổ phần đào tạo trực tuyến UNICA là thành viên của iNET Corporation với sứ mệnh và mục tiêu chia sẻ kiến thức thực tiễn và kinh nghiệm thực tế tới 10 triệu người dân Việt Nam.&nbsp;</p><p>Chúng tôi cung cấp hệ thống đào tạo trực tuyến giúp Học Viên được kết nối với Chuyên gia mọi lúc mọi nơi thông qua nền tảng Unica.vn; các doanh nghiệp triển khai đào tạo nội bộ nhanh chóng với nền tảng Acabiz.vn và giải pháp dạy học trực tuyến với nền tảng Edubit.vn.&nbsp;</p><p>Thành lập từ năm 2017, trải qua 06 năm hoạt động, Unica đã và đang nhận được sự quan tâm của rất nhiều bạn trẻ cũng như các doanh nghiệp và đối tác với hơn 1000 giảng viên, hơn 2000 khóa học &amp; hơn 500.000 học viên.&nbsp;</p><p>Tại Unica, chúng tôi luôn chào đón những nhân tố tài năng và tâm huyết với sứ mệnh "nâng cao giá trị tri thức, phục vụ hàng triệu người Việt Nam"&nbsp;</p>', N'https://cdn-new.topcv.vn/unsafe/135x/filters:format(webp)/https://static.topcv.vn/company_logos/cong-ty-co-phan-dao-tao-truc-tuyen-unica-592566f2a73be_rs.jpg')
INSERT [dbo].[Employer] ([E_id], [E_name], [E_email], [E_password], [E_contactPerson], [E_phone], [E_address], [E_about], [E_image]) VALUES (N'e03', N'Công nghệ và Sáng tạo Rocket', N'rocket@gmail.com', N'123', NULL, N'0886601884', N'Cà Mau', N'<p>Là một start-up trong lĩnh vực IT, nghiên cứu và phát triển các công nghệ mới như AR, VR, MR có khả năng phát triển mạnh mẽ trong 2-10 năm tới.</p><p>Với sứ mệnh: Live tech for life. Chúng tôi mong muốn áp dụng những công nghệ mới nhất để nó hữu ích cho cuộc sống.</p><p>Các lĩnh vực chúng tôi đang triển khai cho khách đều là áp dụng công nghệ vào giáo dục, nghiên cứu y học và đào tạo training nghiệp vụ cho các công ty công nghiệp số một Nhật về cơ khí, oto.</p><p>Thế mạnh của công ty nằm ở kỹ thuật nên vào đây bạn được phát triển và trao cơ hội nhiều.</p><p>Đội ngũ nhân viên Rocket đều là những nhân sự có trình độ chuyên môn cao, luôn làm việc với tất cả sự nhiệt huyết và sức sáng tạo của tuổi trẻ đã xây dựng nên 1 văn hoá vô cùng trẻ trung và năng động. Đến với Rocket, bạn sẽ được trải nghiệm môi trường tuyệt vời, với chế độ đãi ngộ xứng đáng và đạt được thành công vượt mong đợi!</p>', N'https://cdn-new.topcv.vn/unsafe/80x/filters:format(webp)/https://static.topcv.vn/company_logos/u1hG8YQHzODPwxTJy2TDBf09NqeploCl_1664952814____119b3fe3501c7fad2e3736c4669e4057.jpg')
INSERT [dbo].[Employer] ([E_id], [E_name], [E_email], [E_password], [E_contactPerson], [E_phone], [E_address], [E_about], [E_image]) VALUES (N'e04', N'24H MEDIA VIỆT NAM', N'media@gmail.com', N'123', NULL, N'0886601884', N'Hà Nội', N'<p>24hMedia là đơn vị cung cấp cung cấp dịch vụ Digital Marketing, với mục đích nâng cao nhận diện thương hiệu và thúc đẩy hiệu quả kinh doanh sản phẩm, dịch vụ của các nhãn hàng.</p><p>GIÁ TRỊ CỐT LỖI CỦA 24H MEDIA</p><p>Chuyên nghiệp: Vững chuyên môn, hiểu rõ nhiệm vụ, nắm chắc các yêu cầu của khách hàng và luôn có thái độ tích cực, nhiệt tình, thân thiện.</p><p>Uy tín: Lấy chữ tín làm trọng, luôn tâm niệm mọi dịch vụ đều phải đem lại giá trị cho khách hàng.</p><p>Bảo mật an toàn: 24h Media bảo vệ và duy trì tính bảo mật của tất cả các thông tin mà khách hàng đã cung cấp.</p><p>Cập nhật thường xuyên: Cung cấp các giải pháp phù hợp nhất, bắt kịp xu thế Digital Marketing toàn cầu, đem lại lợi ích cao nhất cho khách hàng.</p><p>Sáng tạo: Luôn tìm tòi, nâng cao năng lực bản thân.</p><p>Khuyến mãi: 24h Media cung cấp các chính sách khuyến mãi và chăm sóc khách hàng một cách đặc biệt.</p><p>PHƯƠNG CHÂM CỦA 24H MEDIA</p><p>Với đội ngũ gần 30 nhân sự chuyên nghiệp, có tối thiểu 3 năm kinh nghiệm về Digital Maketing từ các công ty Agency top thị trường trong nước và cả nước ngoài, 24h Media đã và đang khẳng định được vị trí của mình là một trong những công ty Digital Marketing uy tín hàng đầu Việt Nam khi chính là người đứng sau các kế hoạch mang lại sự thành công của hơn 1500 dự án Online cho gần 1000 đối tác trong và ngoài nước thuộc nhiều nhiều ngành nghề khác nhau. Luôn lắng nghe và thấu hiểu những gì khách hàng cần bởi “Thành công của khách hàng là thước đo thành đo thành công của chính mình”. Đó là phương châm làm việc của 24h Media trong suốt thời gian từ khi thành lập đến nay.</p><p>DỊCH VỤ CỦA 24H MEDIA</p><p>Hơn ai hết, 24h Media hiểu rằng, mọi hoạt động Digital Maketing cần được kết hợp để tạo ra một sức mạnh truyền thông tổng thể và đảm bảo giá trị tuyệt vời mà bạn sẽ nhận được từ sự đầu tư. Chính bởi vậy mà 24h Media mang đến các giải pháp tối ưu cho chiến lược Maketing Digital để tạo ra những giá trị có thể đo đếm được và chắc chắn chính bạn cũng sẽ phải thốt lên ngạc nhiên với những gì mà mình đạt được so với chi phí bỏ ra đầu tư.</p>', N'https://cdn-new.topcv.vn/unsafe/80x/filters:format(webp)/https://static.topcv.vn/company_logos/cong-ty-tnhh-24h-media-viet-nam-624696e062452.jpg')
